package fr.opensagres.xdocreport.document.json;

import java.util.Calendar;
import java.util.Date;

import org.junit.Assert;
import org.junit.Test;

import fr.opensagres.xdocreport.core.io.IOUtils;
import fr.opensagres.xdocreport.document.images.IImageProvider;

public class JSONObjectTestCase
{

    @Test
    public void loadFromPojoUpper()
        throws Exception
    {
        Person person = createPerson();

        JSONObject json = new JSONObject( person, true );

        /*
         * Assert.assertEquals( "{" + "\"birthday\":{\"$date\":\"2013-03-04T10:07:05.003+01:00\"},"+
         * "\"married\":true,"+ "\"name\":\"Pascal\","+ "\"age\":20,"+
         * "\"photo\":{\"$img\":\"\"}"
         * + "}", json.toString() );
         */

        Object birthday = json.get( "birthday" );
        Assert.assertNotNull( birthday );
        Assert.assertTrue( birthday instanceof Date );

        Object married = json.get( "married" );
        Assert.assertNotNull( married );
        Assert.assertTrue( married instanceof Boolean );
        Assert.assertTrue( (Boolean) married );

        Object name = json.get( "name" );
        Assert.assertNotNull( name );
        Assert.assertTrue( name instanceof String );
        Assert.assertEquals( "Pascal", name );

        Object age = json.get( "age" );
        Assert.assertNotNull( age );
        Assert.assertTrue( age instanceof Integer );
        Assert.assertEquals( 20, ( (Integer) age ).intValue() );

        Object photo = json.get( "photo" );
        Assert.assertNotNull( photo );
        Assert.assertTrue( photo instanceof IImageProvider );

        System.err.println( json );
    }

    @Test
    public void loadFromPojo()
        throws Exception
    {
        Person person = createPerson();

        JSONObject json = new JSONObject( person, false );

        /*
         * Assert.assertEquals( "{" + "\"Birthday\":{\"$date\":\"2013-03-04T10:07:05.003+01:00\"},"+
         * "\"Married\":true,"+ "\"Name\":\"Pascal\","+ "\"Age\":20,"+
         * "\"Photo\":{\"$img\":\"\"}"
         * + "}", json.toString() );
         */

        Object birthday = json.get( "Birthday" );
        Assert.assertNotNull( birthday );
        Assert.assertTrue( birthday instanceof Date );

        Object married = json.get( "Married" );
        Assert.assertNotNull( married );
        Assert.assertTrue( married instanceof Boolean );
        Assert.assertTrue( (Boolean) married );

        Object name = json.get( "Name" );
        Assert.assertNotNull( name );
        Assert.assertTrue( name instanceof String );
        Assert.assertEquals( "Pascal", name );

        Object age = json.get( "Age" );
        Assert.assertNotNull( age );
        Assert.assertTrue( age instanceof Integer );
        Assert.assertEquals( 20, ( (Integer) age ).intValue() );

        Object photo = json.get( "Photo" );
        Assert.assertNotNull( photo );
        Assert.assertTrue( photo instanceof IImageProvider );

        System.err.println( json );
    }

    @Test
    public void loadFromJson()
        throws Exception
    {
        String img = IOUtils.toString( JSONObjectTestCase.class.getResourceAsStream( "PascalLeclercq.jpg.txt" ) );
        String s =
            "{" + "\"birthday\":{\"$date\":\"2013-03-02T02:02:02.002+01:00\"}," + "\"married\":true,"
                + "\"name\":\"Pascal\"," + "\"age\":20," + "\"photo\":{\"$img\":\"" + img + "\"}" + "}";
        JSONObject json = new JSONObject( s );

        Object birthday = json.get( "birthday" );
        Assert.assertNotNull( birthday );
        Assert.assertTrue( birthday instanceof Date );

        Object married = json.get( "married" );
        Assert.assertNotNull( married );
        Assert.assertTrue( married instanceof Boolean );
        Assert.assertTrue( (Boolean) married );

        Object name = json.get( "name" );
        Assert.assertNotNull( name );
        Assert.assertTrue( name instanceof String );
        Assert.assertEquals( "Pascal", name );

        Object age = json.get( "age" );
        Assert.assertNotNull( age );
        Assert.assertTrue( age instanceof Integer );
        Assert.assertEquals( 20, ( (Integer) age ).intValue() );

        Object photo = json.get( "photo" );
        Assert.assertNotNull( photo );
        Assert.assertTrue( photo instanceof IImageProvider );

        System.err.println( json );
        // Assert.assertEquals( true, married );
    }

    private Person createPerson()
    {
        Person person = new Person();
        person.setMarried( true );
        person.setName( "Pascal" );
        person.setAge( 20 );
        Calendar date = Calendar.getInstance();
        date.set( Calendar.YEAR, 2013 );
        date.set( Calendar.MONTH, Calendar.MARCH );
        date.set( Calendar.DAY_OF_MONTH, 4 );
        date.set( Calendar.HOUR_OF_DAY, 10 );
        date.set( Calendar.MINUTE, 7 );
        date.set( Calendar.SECOND, 5 );
        date.set( Calendar.MILLISECOND, 3 );
        person.setBirthday( date.getTime() );
        person.setPhoto( JSONObjectTestCase.class.getResourceAsStream( "PascalLeclercq.jpg" ) );
        return person;
    }
}